import React, { useState, useEffect } from 'react';
import { Box, Button, Typography, TextField, LinearProgress, Container, Grid } from '@mui/material';
import { styled, ThemeProvider } from '@mui/material/styles';
import { theme, infoSectionStyles, infoBoxStyles, gameButtonStyles } from './Theme';
import statsimg from "../assets/voilet.png";
import axios from 'axios';

const InfoSection = styled('div')(infoSectionStyles);
const InfoBox = styled('div')(infoBoxStyles);
const GameButton = styled(Button)(gameButtonStyles);

const LetterDisplay = styled(Typography)(({ theme }) => ({
  fontWeight: 'bold',
  color: theme.palette.primary.main,
  marginBottom: theme.spacing(3),
  userSelect: 'none',
}));

const LevelDisplay = styled(Typography)(({ theme }) => ({
  fontWeight: 'bold',
  color: theme.palette.secondary.main,
  marginTop: theme.spacing(2),
}));

const SequenceMemory = () => {
  const [level, setLevel] = useState(1);
  const [sequence, setSequence] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [showSequence, setShowSequence] = useState(false);
  const [testOver, setTestOver] = useState(false);
  const [testStarted, setTestStarted] = useState(false);
  const [progress, setProgress] = useState(100);
  const [userId, setUserId] = useState('');
  const [testResults, setTestResults] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const storedUser = localStorage.getItem('user');
        if (!storedUser) { return; }
        const parsedUser = JSON.parse(storedUser);
        const userId = parsedUser._id;
        if (!userId) { return; }
        setUserId(userId);

        const response = await axios.get(`https://yourbenchmark.onrender.com/users/${userId}`);
        setTestResults(response.data.testResults);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const saveTestResults = async () => {
    try {
      const currentTestResults = testResults || {};
      const sequenceMemoryData = currentTestResults.sequenceMemory || { noOfTests: 0, total: 0, min: Infinity, max: 0, avg: 0 };
      
      const score = level - 1;
      const newNoOfTests = sequenceMemoryData.noOfTests + 1;
      const newTotal = sequenceMemoryData.total + score;
      const newMin = Math.min(sequenceMemoryData.min, score);
      const newMax = Math.max(sequenceMemoryData.max, score);
      const newAvg = newTotal / newNoOfTests;

      currentTestResults.sequenceMemory = {
        noOfTests: newNoOfTests,
        total: newTotal,
        min: newMin,
        max: newMax,
        avg: newAvg,
      };

      await axios.patch(`http://localhost:5000/users/${userId}`, {
        testResults: currentTestResults,
      });
      console.log('Test results saved successfully');
    } catch (error) {
      console.error('Error saving test results:', error);
    }
  };

  const generateRandomLetter = () => {
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ123456789';
    return letters.charAt(Math.floor(Math.random() * letters.length));
  };

  const startTest = (currentLevel) => {
    setTestStarted(true);
    const newSequence = [...sequence, generateRandomLetter()];
    setSequence(newSequence);
    setShowSequence(true);
    
    const duration = 1500;
    setProgress(100);

    setTimeout(() => {
      setShowSequence(false);
    }, duration);

    // Progress bar logic
    let progressInterval = setInterval(() => {
        setProgress(prev => {
            if (prev <= 0) {
                clearInterval(progressInterval);
                return 0;
            }
            return prev - (100 / (duration / 100));
        });
    }, 100);
  };

  const handleSubmit = () => {
    if (userInput.toUpperCase() === sequence.join('')) {
      const nextLevel = level + 1;
      setLevel(nextLevel);
      setUserInput('');
      startTest(nextLevel);
    } else {
      saveTestResults();
      setTestOver(true);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !showSequence && testStarted && !testOver) {
      handleSubmit();
    }
  };

  const restartTest = () => {
    setLevel(1);
    setSequence([]);
    setTestOver(false);
    setUserInput('');
    setTestStarted(false); // Reset to initial start screen
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [testStarted, showSequence, userInput, sequence]);

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ bgcolor: 'background.default', minHeight: '100vh', py: 4 }}>
        <Container maxWidth="sm">
          <Box sx={{ textAlign: 'center', py: 4 }}>
            {!testOver ? (
              <>
                <Typography variant="h3" gutterBottom fontWeight="bold" color="primary">
                  Sequence Memory
                </Typography>
                <LevelDisplay variant="h6" gutterBottom>
                  Level {level}
                </LevelDisplay>

                {showSequence ? (
                  <>
                    <LetterDisplay variant="h3" gutterBottom>
                      {sequence[sequence.length - 1]}
                    </LetterDisplay>
                    <LinearProgress
                      variant="determinate"
                      value={progress}
                      sx={{ width: '80%', margin: '20px auto', height: 10 }}
                    />
                  </>
                ) : (
                  <>
                    {testStarted && (
                      <Grid container direction="column" spacing={2} alignItems="center">
                        <Grid item>
                          <Typography variant="h5" gutterBottom>
                            What was the sequence?
                          </Typography>
                        </Grid>
                        <Grid item>
                          <TextField
                            value={userInput}
                            onChange={(e) => setUserInput(e.target.value)}
                            variant="outlined"
                            placeholder="Enter the sequence"
                            inputProps={{
                              style: { textAlign: 'center', fontSize: '24px' },
                              autoFocus: true
                            }}
                          />
                        </Grid>
                        <Grid item>
                          <GameButton variant="contained" color="primary" onClick={handleSubmit}>
                            Submit
                          </GameButton>
                        </Grid>
                      </Grid>
                    )}
                  </>
                )}
              </>
            ) : (
              <>
                <Typography variant="h4" gutterBottom fontWeight="bold" color="primary">
                  Test Over!
                </Typography>
                <Typography variant="h5">
                  You reached Level {level - 1}
                </Typography>
                <GameButton variant="contained" color="primary" onClick={restartTest} sx={{ mt: 2 }}>
                  Restart
                </GameButton>
              </>
            )}

            {!testStarted && (
              <GameButton variant="contained" color="primary" onClick={() => startTest(level)} size="large">
                Start Game
              </GameButton>
            )}
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
};

export default SequenceMemory;
